import React, { useEffect, useState } from "react";
import axios from "axios";

function ProductList() {
  const URL = "http://localhost:8080/api/products/findAllProductBuying";
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProductList();
  }, []);

  const fetchProductList = async () => {
    try {
      const response = await axios.get(URL, {
        headers: {
          "Access-Control-Allow-Origin": "http://localhost:3000/",
          // Add other headers if needed
        },
      }); // Replace with your API endpoint
      setProducts(response.data);
      const deleteProduct = async (productId) => {
        try {
          await axios.delete(`http://localhost:8080/api/products/delete${productId}`);
          fetchProductList(); // Refresh the product list after deletion
        } catch (error) {
          console.error('Error deleting product:', error);
        }
      };
    
      const updateProduct = async (productId, updatedData) => {
        try {
          await axios.put(`http://localhost:8080/api/products//update${productId}`, updatedData);
          fetchProductList(); // Refresh the product list after update
        } catch (error) {
          console.error('Error updating product:', error);
        }
      };
    
      const insertProduct = async (newProductData) => {
        try {
          await axios.post('http://localhost:8080/api/products/insert', newProductData);
          fetchProductList(); // Refresh the product list after insertion
        } catch (error) {
          console.error('Error inserting product:', error);
        }
      };

    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      {products.map((product) => (
        <div key={product.id}>
          <h3>{product.name}</h3>
          <img src={product.image} alt="" style={{height: "100px", width: "100px"}}/>
          <p>{product.type}</p>
          <p>{product.desc}</p>
          <p>{product.price}</p>

        </div>
      ))}
    </div>
  );
}

export default ProductList;
